import {useEffect, useState} from "react";

function CardArea() {
    const [users, setUsers] = useState([]);
    const [index, setIndex] = useState(0);
    const [slideIndex, setSlideIndex] = useState(0);

    useEffect(() => {
        // Simulaci칩n de llamada a la API
        const data = [{
            id: "1",
            slides: [
                {
                    image: "gorka.png",
                    title: "Sarah",
                    subtitle: "22 a침os",
                    extra: null
                },
            ],
        }]
        setUsers(data);
    }, []);

    const user = users[index];
    const currentSlide = user?.slides?.[slideIndex];

    if (!user || !currentSlide) return <p>Cargando...</p>;

    const nextSlide = () => {
        setSlideIndex((prev) =>
            prev + 1 < user.slides.length ? prev + 1 : 0
        );
    };

    const prevSlide = () => {
        setSlideIndex((prev) =>
            prev - 1 >= 0 ? prev - 1 : user.slides.length - 1
        );
    };

    const nextUser = () => {
        setIndex((prev) => (prev + 1) % users.length);
        setSlideIndex(0); // reset al primer slide del siguiente user
    };

    return (
        <div className="card-area">
            <div className="card">
                <button className="arrow left" onClick={prevSlide} aria-label="Anterior">
                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                        <path d="M15 18L9 12L15 6" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                </button>
                <img src={currentSlide.image} className="photo" alt="slide" />
                <button className="arrow arrow--right" onClick={nextSlide}>&rsaquo;</button>

                <div className="card__info">
                    <h3>{currentSlide.title}</h3>
                    <p>{currentSlide.subtitle}</p>

                    {currentSlide.extra && (
                        <img src={currentSlide.extra} className="artist-avatar" alt="extra" />
                    )}
                </div>

                <div className="card__actions">
                    <button className="btn btn--no">&times;</button>
                    <button className="btn btn--yes" onClick={nextUser}>&#10084;</button>
                </div>
            </div>
        </div>
    )
}

export default CardArea;
