import {useEffect, useState} from "react";

function CardArea() {
    const [users, setUsers] = useState([]);
    const [index, setIndex] = useState(0);
    const [slideIndex, setSlideIndex] = useState(0);

    useEffect(() => {
        // Simulaci칩n de llamada a la API
        const data = [{
            id: "1",
            slides: [
                {
                    image: "gorka.png",
                    title: "Sarah",
                    subtitle: "22 a침os",
                    extra: null
                },
            ],
        }]
        setUsers(data);
    }, []);

    const user = users[index];
    const currentSlide = user?.slides?.[slideIndex];

    if (!user || !currentSlide) return <p>Cargando...</p>;

    const nextSlide = () => {
        setSlideIndex((prev) =>
            prev + 1 < user.slides.length ? prev + 1 : 0
        );
    };

    const prevSlide = () => {
        setSlideIndex((prev) =>
            prev - 1 >= 0 ? prev - 1 : user.slides.length - 1
        );
    };

    const nextUser = () => {
        setIndex((prev) => (prev + 1) % users.length);
        setSlideIndex(0); // reset al primer slide del siguiente user
    };

    return (
        <div className="card-area">
            <div className="card" style={{ backgroundImage: `url(${currentSlide.image})` }}>
                <button className="arrow left" onClick={prevSlide} aria-label="Anterior">
                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                        <path d="M15 18L9 12L15 6" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                </button>
                <button className="arrow right" onClick={nextSlide} aria-label="Siguiente">
                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                        <path d="M9 6L15 12L9 18" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                </button>

                <div className="card__info">
                    <h3>{currentSlide.title}</h3>
                    <p>{currentSlide.subtitle}</p>

                    {currentSlide.extra && (
                        <img src={currentSlide.extra} className="artist-avatar" alt="extra" />
                    )}
                </div>

                <div className="card__actions">
                    <button className="btn btn--no" aria-label="No me gusta">
                        <svg width="28" height="28" viewBox="0 0 24 24" fill="none">
                            <path d="M18 6L6 18" stroke="#FF3E3E" strokeWidth="2" strokeLinecap="round"/>
                            <path d="M6 6L18 18" stroke="#FF3E3E" strokeWidth="2" strokeLinecap="round"/>
                        </svg>
                    </button>

                    <button className="btn btn--yes" aria-label="Me gusta">
                        <svg width="28" height="28" viewBox="0 0 24 24" fill="none">
                            <path d="M12 21C12 21 4 13.5 4 8.5C4 5.5 6.5 4 8.5 4C10 4 12 5.5 12 5.5C12 5.5 14 4 15.5 4C17.5 4 20 5.5 20 8.5C20 13.5 12 21 12 21Z"
                                  stroke="#00C851" strokeWidth="2" fill="none" />
                        </svg>
                    </button>
                </div>
            </div>
        </div>
    )
}

export default CardArea;
