import {Avatar} from "@mui/material";
import {useState} from "react";

function Sidebar() {
    const [sidebarView, setSidebarView] = useState("matches");
    const matches = [
        {
            id: "1",
            image: "gorka.png",
            name: "Gorka",
        },
        {
            id: "2",
            image: "gorka.png",
            name: "Gorka",
        }
    ];
    const conversations = ['Claudia', 'Èrika', 'Lola']

    return (
        <aside className="sidebar">
            <header className="sidebar__header">
                <Avatar
                    alt="Gorka"
                    src="gorka.png"
                    sx={{ width: 56, height: 56 }}
                />
                <span className="username">Gorka</span>
            </header>

            <nav className="sidebar__nav">
                <button className="pill" onClick={() => setSidebarView("matches")}>Matches</button>
                <button className="pill" onClick={() => setSidebarView("messages")}>Mensajes</button>
            </nav>

            {sidebarView === "matches" ? (
                <ul className="matches">
                    {matches.map((match) => (
                        <li key={match.id} className="match">
                            <Avatar
                                variant= "rounded"
                                className="match__avatar"
                                alt="Gorka"
                                src={match.image}
                            />
                            <span className="match__name">{match.name}</span>
                        </li>
                    ))}
                </ul>
                ): (
                <ul className="matches">
                    {conversations.map((name) => (
                        <li key={name} className="match">
                            <Avatar
                                variant="rounded"
                                className="match__avatar"
                                alt="Gorka"
                                src="gorka.png"
                            />
                            <span className="match__name">{name}</span>
                        </li>
                    ))}
                </ul>
            )
            }
        </aside>
    );
}

export default Sidebar;
