function Sidebar() {
    const matches = ['Andrea', 'Lola', 'Júlia'];

    return (
        <aside className="sidebar">
            <header className="sidebar__header">

                <Avatar
                    alt="Nombre del usuario"
                    src="https://example.com/foto.jpg"
                    sx={{ width: 56, height: 56 }}
                />
                <span className="username">Gorka</span>
            </header>

            <nav className="sidebar__nav">
                <button className="pill pill--active">Matches</button>
                <button className="pill">Mensajes</button>
            </nav>

            <ul className="matches">
                {matches.map((name) => (
                    <li key={name} className="match">
                        <div className="match__avatar"/>
                        <span>{name}</span>
                    </li>
                ))}
            </ul>
        </aside>
    );
}

export default Sidebar;
